import React, {Component} from 'react';
import './App.css';

class App extends Component {
  constructor(){
    super();

    this.state = {
      string: "Hello Guys its Ankit"
    }
  }
  render(){
    return(
      <div className="">
        <center>
          <h1>{ this.state.string }</h1>
          <button onClick={() => this.setState({ string: "Hello user! Welcome to React"})}>Change Text</button>
        </center>
      </div>
    )

  }
}

export default App;
